import { NavLink } from "react-router-dom";

const NavLi = (props) => {
  console.log(props.active);
  return (
    <li className="nav-item nav-li">
      {/* <NavLink
        className={({ isActive }) => isActive ? "nav-link " + props.active : "nav-link"}
        to={`/${props.children}`}>
        {props.children}
      </NavLink> */}
      <NavLink
        className="nav-link"
        activeClassName={`nav-link ${props.active}`}
        to={`/${props.children}`}>
        {props.children}
      </NavLink>
    </li>
  );
}

export default NavLi;
